document.addEventListener('DOMContentLoaded', function() {
  const demoImage = document.getElementById('demoImage');
  const demoCaption = document.getElementById('demoCaption');

  const images = [
    { src: 'https://picsum.photos/350/250?random=1', caption: 'Beautiful nature scene' },
    { src: 'https://picsum.photos/350/250?random=2', caption: 'Urban architecture' },
    { src: 'https://picsum.photos/350/250?random=3', caption: 'Peaceful landscape' }
  ];

  let currentIndex = 0;

  demoImage.addEventListener('click', function() {
    currentIndex = (currentIndex + 1) % images.length;
    demoImage.src = images[currentIndex].src;
    demoCaption.textContent = images[currentIndex].caption;
  });
});

