files.script.js:
// Accessibility-focused JavaScript
document.addEventListener('DOMContentLoaded', function() {
  // Announce emphasis changes to screen readers
  function announceEmphasis() {
    const emphasisElements = document.querySelectorAll('em, strong');

    emphasisElements.forEach(element => {
      // Add ARIA labels for better screen reader support
      if (element.tagName.toLowerCase() === 'em') {
        element.setAttribute('aria-label', `Emphasized: ${element.textContent}`);
      } else if (element.tagName.toLowerCase() === 'strong') {
        element.setAttribute('aria-label', `Important: ${element.textContent}`);
      }
    });
  }

  // Initialize accessibility features
  announceEmphasis();

  // Add keyboard navigation support
  document.addEventListener('keydown', function(event) {
    // Skip to next emphasis with Ctrl+E
    if (event.ctrlKey && event.key === 'e') {
      event.preventDefault();
      const emphasisElements = Array.from(document.querySelectorAll('em, strong'));
      const currentFocus = document.activeElement;

      let currentIndex = emphasisElements.indexOf(currentFocus);
      let nextIndex = (currentIndex + 1) % emphasisElements.length;

      if (nextIndex === 0 && currentIndex === -1) {
        nextIndex = 0;
      }

      if (emphasisElements[nextIndex]) {
        emphasisElements[nextIndex].focus();
        emphasisElements[nextIndex].scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    }
  });

  // Make emphasis elements focusable for keyboard navigation
  const emphasisElements = document.querySelectorAll('em, strong');
  emphasisElements.forEach(element => {
    element.setAttribute('tabindex', '0');
    element.setAttribute('role', 'text');
  });

  // Add live region for dynamic announcements
  const liveRegion = document.createElement('div');
  liveRegion.setAttribute('aria-live', 'polite');
  liveRegion.setAttribute('aria-atomic', 'true');
  liveRegion.style.position = 'absolute';
  liveRegion.style.left = '-10000px';
  liveRegion.style.width = '1px';
  liveRegion.style.height = '1px';
  liveRegion.style.overflow = 'hidden';
  document.body.appendChild(liveRegion);

  console.log('Accessibility features loaded. Press Ctrl+E to navigate between emphasis elements.');
});

