let clickCount = 0;
const greetingParagraph = document.getElementById('greeting');
const countSpan = document.getElementById('count');

const messages = [
  'Click me to change this paragraph!',
  'Great job! You clicked me!',
  'Another click! Paragraphs can be interactive!',
  'Keep clicking to see more messages!',
  'HTML paragraphs are the foundation of web content!'
];

greetingParagraph.addEventListener('click', function() {
  clickCount++;
  countSpan.textContent = clickCount;

  const messageIndex = clickCount % messages.length;
  greetingParagraph.textContent = messages[messageIndex];
});

